import { Carousel, Col, Container, Row, ListGroup } from 'react-bootstrap'
import dishes from '../data/menu.json'
import {Component} from 'react'

class Home extends Component {
    state = {
        selected:null
    }
    render(){
        return(
    <Container>
        <Row className="justify-content-center mt-3">
            <Col xs={12} md={6}>
                <Carousel>
                    {/* MAP */}
                    {
                        dishes.map((dish, i) => (
                            <Carousel.Item key={dish.id} onClick ={() => this.setState({selected:dish.name})}>
                                <img
                                    className="d-block w-100"
                                    src={dish.image}
                                    alt={"slide number " + (i + 1)}
                                />
                                <Carousel.Caption>
                                    <h3>{dish.name}</h3>
                                    <p>{dish.description}</p>
                                </Carousel.Caption>
                            </Carousel.Item>)
                        )
                    }
                </Carousel>
            </Col>
        </Row>
        <Row>
            <Col xs={12} md={6}>

                            <ListGroup>
                        {
                            this.state.selected ? this.selected.comments.map(c=>( 
                            <ListGroup.Item key ={c.id}>{c.comment}</ListGroup.Item>
                             )): <p>You have not selected anything</p>
                        }
                        </ListGroup>
            </Col>
        </Row>
    </Container>
    )}
                }

export default Home
